package vn.edu.iuh.fit.service;import org.springframework.cache.annotation.CacheEvict;import org.springframework.cache.annotation.Cacheable;import org.springframework.stereotype.Service;import vn.edu.iuh.fit.model.Book;import vn.edu.iuh.fit.repositories.BookRepository;import java.util.List;import java.util.Optional;@Servicepublic class BookService {    private final BookRepository bookRepository;    public BookService(BookRepository bookRepository) {        this.bookRepository = bookRepository;    }    @Cacheable("books")    public List<Book> getAllBooks() {        return bookRepository.findAll();    }    @Cacheable(value = "book", key = "#id")    public Optional<Book> getBookById(int id) {        return bookRepository.findById(id);    }    public Book createBook(Book book) {        return bookRepository.save(book);    }    @CacheEvict(value = "book", key = "#id")    public Book updateBook(int id, Book book) {        book.setId(id);        return bookRepository.save(book);    }    @CacheEvict(value = "book", key = "#id")    public void deleteBookById(int id) {        bookRepository.deleteById(id);    }}